const float fragCount = 7.;
const float antiAliasScope = 0.1;

vec3 getColorFromFactor(float factor) {
    return
          factor < 0. ? vec3(1., 0., 0.)
        : factor < 1. ? vec3(1., .5, 0.)
        : factor < 2. ? vec3(1., 1., 0.)
        : factor < 3. ? vec3(0., 1., 0.)
        : factor < 4. ? vec3(0., 1., 1.)
        : factor < 5. ? vec3(0., 0., 1.)
        : factor < 6. ? vec3(.6, 0., 1.)
        : vec3(1., 0., 0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = iResolution.xy / 2.;
    float dist = distance(center, fragCoord);
    float factor = fract(dist / (iResolution.x / 2.) * 2. - iTime / 3.) * fragCount;
    float antiAliasFactor = clamp(fract(factor) / antiAliasScope, 0., 1.);

    vec3 rgb = getColorFromFactor(factor);
    if (antiAliasFactor < 1.) {
        vec3 rgbPrev = getColorFromFactor(factor - antiAliasScope);
        rgb = mix(rgbPrev, rgb, antiAliasFactor);
    }
    fragColor = vec4(rgb, 1.);
}
