// inpired by Box-Muller Method

#define A 1e5
#define B 1e5

float noise( float a, float b, vec2 c )
{
    float k = c.x * c.y;

	c.x = 2. * cos(a * k * c.y) - 1.;
	c.y = 2. * sin(b * k * c.x) - 1.;

    c *= (1e3 + iTime) * 0.002;

	k = fract(dot(c,c));

	return 1. - sqrt(-2. * log(k) / k);
}

void mainImage( out vec4 f, vec2 g )
{
    g /= iResolution.y;

    f += -f + noise(A,B,g);
}
