void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 R = iResolution.xy;
    vec2 uv = vec2( fragCoord - .5*R ) / R.y  *5.;
    uv.x+=sin(uv.y*t*.5);

	fragColor = mix(vec4(sin(dot(uv,uv)-t)),
                    vec4(-uv.x-uv.y, uv.x, uv.y, 1.),
                    .2);
}
