// gigatron France for Shadertoy 2016 !
// simple pattern and color bypass ;
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv= fragCoord.xy/iResolution.xy;    
    float a=0.4; // color bypass factor !
    // simple patern
	float scl  = mod((fragCoord.y + fragCoord.y) * 0.5, 2.0);
          scl *= mod((fragCoord.x + fragCoord.x) * 0.5, 2.0);
          scl *= mod((fragCoord.x + fragCoord.y) * 0.5, 2.0);


    vec3 tx=texture(iChannel0,uv).stp; // s'il te plait !

    (tx.r>a*1.7 || tx.g>a*2. || tx.b>a*2.) ? fragColor = scl * vec4(tx, 1.0)
    // ternary OR condition !  
    :fragColor = scl * vec4(0);

   // variant  
   // :fragColor = scl * vec4(tx.rgb/fract(uv.y), 1.0);

}
