// lattice from https://www.shadertoy.com/view/Xl3GDS

void mainImage( out vec4 O,  vec2 U )
{
    O-=O;
    for (float i=.1; i<1.01; i+=.1) {
        float v = .3* ( .5+.5*sin(iDate.w+6.3*i) );
        //              focus        lattice     
        O +=  smoothstep(-v,v, length( fract( U/i/70. - iDate.w* i ) -.5 )-.45) 
      //O += smoothstep(-v*6.,v*6., sin(15.*length( fract( U/i/70. - iDate.w* i ) -.5 ))-.8)
             * (i - O);                                    // color
          // * (.5+ .5* cos(6.3*i+vec4(0,-2,2,0)) - O);    // color
    }
}
