vec3 compAlpha(vec4 i1, vec4 i2);
vec3 toVec3(vec4 v);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(compAlpha(texture(iChannel0, uv.xy), texture(iChannel1, uv.xy)), 1.0);
}

vec3 compAlpha(vec4 i1, vec4 i2)
{
    vec3 col;

    if(i1.a > i2.a)
    {
        col = toVec3(i1);
    }
    else
    {
        col = toVec3(i2);
    }

    if(i1.a == i2.a)
    {
        col = toVec3(i1);
    }

    return col;
}

vec3 toVec3(vec4 v)
{
    return vec3(v.x, v.y, v.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(0.0,0.0,uv.x / 1.0,uv.x / 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
