#define PI 3.14159265

float ripples = 0.35;
vec2 scale = vec2(10.0, 10.0);
bool trueDistortion = true;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float v = 0.0;
    vec2 distort = uv * scale - scale/2.0;
    v += sin(sqrt(distort.x*distort.x + distort.y * distort.y) + iTime + 1.0);

    distort = vec2(sin(PI*v*ripples),cos(PI*v*ripples));
    vec2 texCord;

    if(trueDistortion){
    	texCord = uv*distort;
    }else{
    	texCord = uv+distort;
    }

    vec4 col = texture(iChannel0, texCord);

	fragColor = col;
}
