vec4 Vignetting_Hermit(vec2 uv, vec2 vignetUV, float start, float end, vec4 srcColor, vec4 fadeColor)
{
    float dist = smoothstep(start, end, distance( uv, vignetUV ) );
    float fade = clamp(dist, 0.0, 1.0);

    return mix(srcColor, fadeColor, fade);
}

vec4 Vignetting_Linear(vec2 uv, vec2 vignetUV, float start, float end, vec4 srcColor, vec4 fadeColor)
{
    float dist = mix(start, end, distance( uv, vignetUV ) );
    float fade = clamp(dist, 0.0, 1.0);

    return mix(srcColor, fadeColor, fade);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv			= fragCoord.xy / iResolution.xy;

    float end		= clamp(1.0 + 0.5 * sin(iTime), 0.0, 1.0);
    vec4 srcColor	= vec4(uv, 0.5 + 0.5 * sin(iTime), 1.0);
    vec4 fadeColor	= vec4(1.0, 1.0, 1.0, 1.0);

  	vec4 res = Vignetting_Hermit(uv, vec2(0.5, 0.5), 0.0, end, srcColor, fadeColor);
//  vec4 res = Vignetting_Linear(uv, vec2(0.5, 0.5), 0.0, end, srcColor, fadeColor);

	fragColor = vec4(res.rgb, 1.0);
}
