void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 solidRed = vec4(1.0,0.0,0.0,1.0); 
    vec4 solidYellow = vec4(1.0,1.0,0.0,0.0);
    vec4 solidBlue = vec4(0.0,0.0,1.0,0.0);

	vec2 coord = fragCoord.xy/iResolution.xy; //asign the coordinates of the pixel

    if (coord.x < 0.33){
        fragColor = solidRed;
    }
    else if(coord.x>0.33 && coord.x<0.66){
        fragColor = solidYellow;
    }  
    else{
    	fragColor = solidBlue;
    }
}
