//from https://www.shadertoy.com/view/ltfSRr
float message(vec2 uv) { // to alter in the icon
    uv-=vec2(1.,10.); if ((uv.x<0.)||(uv.x>=32.)||(uv.y<0.)||(uv.y>=3.)) return -1.; 
    int i=1, bit=int(pow(2.,floor(24.-uv.x)));
    if (int(uv.y)==2) i=  3626848/bit; // 00110111 01010111 01100000 
    if (int(uv.y)==1) i=  2446672/bit; // 00100101 01010101 01010000
    if (int(uv.y)==0) i=  6780256/bit; // 01100111 01110101 01100000
 	return float(i-2*(i/2));
}


void mainImage( out vec4 o, in vec2 u ) {
    if (iTime<2.) {float c=message(u/8.);if(c>=0.){o=vec4(c);return;}}


    u /= iResolution.xy;
	o = vec4(u,.5,1.0);

}

float v = 1e3; //bug starts at 1e4

vec2 mainSound( in int samp, float t ){
    v = floor(mod(t,3.)*v)/v;
    //v = floor(sin(t)*v)/v; //another showcase
    return vec2( sin(6.2831*440.0*t*v)*.3 );
}
