void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    uint s = uint( U.y/R.y*exp2(24.)); // set many bits

    int i = int(U.x*1e-29+43.);        // = 43
  //int i =43;
  //int i = int(U.x*0.+43.);           // = 43

  //int i = U.x < R.x/2. ? int(U.x*1e-29+43.) : int(U.x*0.+43.);

    O = vec4( ( s >> i ) & 1u );       // shift and show bit 0
}
