float sdf(vec3 pos, float time)
{
    vec2 polar;
    vec3 local_sp = pos;
    polar.y = atan(local_sp.z, local_sp.x);
    float xzLen = sqrt(local_sp.x * local_sp.x + local_sp.z * local_sp.z);
    polar.x = atan(xzLen, -local_sp.y);

    return length(pos) - 0.7
        - (sin(polar.x*8.0+polar.y*8.0+time) * 0.5 + 0.5)*0.1
        ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float gradient_offset = 0.0001;
    const float MIN_DIST = 0.01;
    const float MAX_DIST = 200.0;

    vec2 uv = fragCoord.xy / iResolution.yy * 2.0;
    vec3 start_pos = vec3(uv.x-1.0, uv.y-1.0, 0.0);
    vec3 camera = vec3(0.0, 0.0,-100.0);
    vec3 direction = normalize(start_pos - camera);

    vec3 pos = start_pos + vec3(-0.8, 0.0, -0.7);

    // ray marching
    float d = 10.0;
    for(int i =0; i < 32; i++) {
	  d = sdf(pos, iTime );
	  pos += direction * d;
	  if(d < MIN_DIST || d > MAX_DIST) break;
    }

    float r = 0.0, g = 0.0, b = 0.0;

    if(d<=MIN_DIST) {

	  // estimate normal based on finite difference approx of gradient
	  vec3 gradient = sdf(pos, iTime) - vec3(
	      sdf(pos + vec3(gradient_offset,.000,.000), iTime),
	      sdf(pos + vec3(.000,gradient_offset,.000), iTime),
	      sdf(pos + vec3(.000,.000,gradient_offset), iTime)
	      );
      	  vec3 normal = normalize( gradient );

	  // red diffuse light
	  r = dot( normal, normalize(vec3(2.0,-4.0,+0.5))) * 0.9;
	  // green diffuse light
	  g = dot( normal, normalize(vec3(-0.4,0.4,+0.4))) * 0.2;
	  // blue diffuse light
	  b = dot( normal, normalize(vec3(0.9,-0.3,+0.4))) * 0.5;
    }
    fragColor = vec4(r, g, b, 1.0);
}
