void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = uv - vec2(.32 + sin(iTime)*0.025*uv.y, .65);

    vec3 col = 0.5 + 0.5*cos(iTime-uv.y-vec3(0,2,4));

    float r = 0.2 + 0.1 * cos(atan(q.y, q.x)*11.0 + 13.0 * q.x + sin(iTime));
    col *= smoothstep(r, r+0.005, length(q));
    r = 0.0225;
    r += 0.002 * cos(80.0*q.y);
    r += exp(-100.0*uv.y);
    col *= 1.0 - (1.0 - smoothstep(r, r+0.0025, abs(q.x - .21*sin(2.2*q.y))))*(1.0 - smoothstep(0.0, 0.1, q.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}
