//COLORS
vec3 background = vec3(0.372, 0.847, 0.866);
vec3 color1 = vec3(1.0,0.0,0.0);
vec3 color2 = vec3(1.0,1.0,0.0);
vec3 color3 = vec3(0.0,0.0,1.0);
//Locations
vec2 houseLoc = vec2(0.5,0.0);
vec2 houseS = vec2(0.5,0.5);

vec2 winLoc = vec2(0.55,0.4);
vec2 win2Loc = vec2(1.15, 0.4);
vec2 winS = vec2(0.1,0.2);

vec2 chLoc = vec2(1.2, 0.85);
vec2 chS = vec2(0.05,0.3); 


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    float aR = iResolution.y/iResolution.x;

    houseLoc*= aR;
    winLoc*= aR;
    win2Loc*= aR;
    chLoc*= aR;

    vec4 bgLayer = vec4(background,1.0);
    vec4 layer1;
    vec4 layer2;
    vec4 layer3;

	float interp1X = step(houseLoc.x, uv.x) - step(houseLoc.x+houseS.x, uv.x);
    float interp1Y = step(houseLoc.y, uv.y) - step(houseLoc.y+houseS.y, uv.y);
    layer1 = vec4(color1,(interp1X*interp1Y));

    float interp2X = step(winLoc.x, uv.x) - step(winLoc.x+winS.x, uv.x);
    float interp2Y = step(winLoc.y, uv.y) - step(winLoc.y+winS.y, uv.y);
    layer2 = vec4(color2,(interp2X*interp2Y));

    float interp3X = step(win2Loc.x, uv.x) - step(win2Loc.x+winS.x, uv.x);
    float interp3Y = step(win2Loc.y, uv.y) - step(win2Loc.y+winS.y, uv.y);
    layer2 += vec4(color2,(interp3X*interp3Y));

	float interp4X = step(chLoc.x, uv.x) - step(chLoc.x+chS.x, uv.x);
    float interp4Y = step(chLoc.y, uv.y) - step(chLoc.y+chS.y, uv.y);
    layer3 = vec4(color3,(interp4X*interp4Y));




    vec4 comp = mix(bgLayer, layer1, layer1.a);
    comp = mix(comp,layer3, layer3.a);
    comp = mix(comp, layer2, layer2.a);

    fragColor = comp;
}
