#define EPSILON pow(10., -4.)
#define bigness 0.01
#define PI 3.1415926
#define light_col vec3(1., 0., 0.)
#define num_lights 1.
vec3 tex (vec3 p, sampler2D t){
    vec3 t1 = texture(t, p.xy).rgb/3.;
    t1+=texture(t, p.xz).rgb/3.;
    t1+=texture(t, p.yz).rgb/3.;
    return t1;
}
float align (float diff, float dim){
    if(diff>=dim){
        return diff-dim;
    } else {
        return 0.;
    }
}
float box3D (vec3 boxPos, vec3 boxSize, vec3 pos){
    float n = align(abs(pos.x-boxPos.x), boxSize.x)+align(abs(pos.y-boxPos.y), boxSize.y);
    n+=align(abs(pos.z-boxPos.z), boxSize.z);//just to keep the lines short
    return n;
}
float sphere (vec3 spherePos, vec3 pos, float radius){
    return length(pos-spherePos)-radius;
}
//Credit to Inigo Quilez for original function-- https://iquilezles.org/articles/distfunctions
float CappedCylinder( vec3 pos, vec3 p, vec2 h )
{
  p = p-pos;
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float cylinder (vec3 pos, vec3 p, float radius, float height){
    vec3 diff = p-pos;
    float yd = abs(diff.y);
    return (length(diff.xz)-radius)+smoothstep(yd, -height*0.5, height*0.6);//+smoothstep(abs(diff.y)-height, 0., -height);
}
float doubleCone (vec3 pos, vec3 p, vec2 s){
    vec3 diff = p-pos;
    float yd = abs(diff.y)-s.y;
    return (length(diff.xz)-s.x)-yd;
}
//useful functions
vec2 rotate2D (vec2 p, float angle){
    float a = atan(p.y, p.x)+angle;
    float m = length(p);
    return vec2(cos(a)*m, sin(a)*m);
}
//from IQ -- https://iquilezles.org/articles/smin
float smin( float a, float b, float k)
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}
float mymin (float a, float b, float k){
    return min(a, b)-abs(a-b)/(k*100.);
}
vec3 calcLight (float t) {
    return vec3(cos(t), sin(t), sin(t*2.))*bigness*2.;
}
vec4 SDF (vec3 p){
    vec3 matCol = vec3(0.);
    float total = 0.;
    float cy1 = CappedCylinder(p, vec3(0.), vec2(1.)*bigness);
    float cy2 = CappedCylinder(vec3(rotate2D(p.xy, iTime*0.5), p.z), vec3(1., 0., 0.)*bigness, vec2(0.1, 5.)*bigness);
    vec3 np = p;
    np.xz = rotate2D(np.xz, iTime*0.7);
    float cy3 = CappedCylinder(np, vec3(1., 0., 0.)*bigness, vec2(0.1, 5.)*bigness);
    np = p;
    np.yz = rotate2D(np.yz, iTime*0.3);
    float cy4 = CappedCylinder(np, vec3(1., 0., 0.)*bigness, vec2(0.1, 5.)*bigness);
    float cut = doubleCone(vec3(0.), p, vec2(1.)*bigness)-0.1*bigness;
    total = max(cy1, -cut);
    total = smin(total, cy2, 0.003);
    total = smin(total, cy3, 0.003);
    total = smin(total, cy4, 0.003);

    //vec3 np = vec3(rotate2D(p.xy, iTime*0.8), p.z);
    // np.xz = rotate2D(np.xz, iTime*0.3);
    //np.yz = rotate2D(np.yz, iTime);
    //float rotatingCube = -bigness*0.2+box3D(vec3(0., sin(iTime)*bigness, 0.), vec3(0.3)*bigness, np);
    // total = smin(total, rotatingCube, 0.001);
    for(float j = 0.; j<num_lights; j++){
        vec3 ligPos = calcLight(iTime*0.5+j);
        float light = sphere(ligPos, p, bigness*0.1);
        if(light<=total){
            matCol = light_col;
        }
        total = min(total, light);
    }

    return vec4(total, matCol);
}
vec4 trace (vec3 o, vec3 r) {
    float t = 0.;
    vec3 p = o;
    for(int i = 0; i<72; i++){
        p = o+r*t;
        float d = SDF(p).x;
        t+=d*0.7;
    }
    return vec4(p, t);
}
//Credit to Jamie Wong http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/#surface-normals-and-lighting
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        SDF(vec3(p.x + EPSILON, p.y, p.z)).x - SDF(vec3(p.x - EPSILON, p.y, p.z)).x,
        SDF(vec3(p.x, p.y + EPSILON, p.z)).x - SDF(vec3(p.x, p.y - EPSILON, p.z)).x,
        SDF(vec3(p.x, p.y, p.z  + EPSILON)).x - SDF(vec3(p.x, p.y, p.z - EPSILON)).x
    ));
}

//made this one myself with some tutorials I found out there on the 
float diffuseLight (vec3 n, vec3 r){
    return dot(n, -r);
}
float specularLight (vec3 n, vec3 r){
    vec3 nr = reflect(r, n);
    return pow(max(dot(nr, -r), 0.), 4.);
}
