/* Thanks to jlfwong for shadertoy tutorials :) */
/**
 * Part 2 Challenges
 * - Change the diffuse color of the sphere to be blue
 * - Change the specual color of the sphere to be green
 * - Make one of the lights pulse by having its intensity vary over time
 * - Add a third light to the scene
 */

const int MAX_MARCHING_STEPS = 256;
const float MIN_DIST = 10.0;
const float MAX_DIST = 500.0;
const float EPSILON = 0.005;

const float SPLIT_VIEWPORT = 1.0;
const float SCENE_TILE = 3.0;

vec4 fragmentTextureColor;
vec3 RMDirection;
vec3 CameraPosition;

/**
 * Signed distance function for a sphere centered at the origin with radius 1.0;
 */
float sphereSDF(vec3 samplePoint, float r) {
//    float off = 1.0 + (sin(iTime + samplePoint.x * 25.0) + cos(iTime + samplePoint.y * 54.0)) * 0.01;
    return length(samplePoint) - r;
}

float udBox( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}


vec4 getTexColor(in vec2 uv)
{    
    vec4 col = texture(iChannel0, uv * 0.0025,0.1);
    uv += iTime * 10.0;
    col += texture(iChannel0, uv * 0.0025, 0.1);
//    col = texture(iChannel0, uv * 0.01, -100.0);
    return col * 1.5;
}


float random(in vec2 uv)
{
    float r = fract(sin(uv.x + uv.y) * 3432.3 + cos(uv.x + uv.y) * 1042.52);
    return r;
}

/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */
float sceneSDF(vec3 samplePoint) {
//    float st = (sin(iTime * 0.25) * SCENE_TILE * 0.5) + (SCENE_TILE * 1.15);
//    samplePoint = mod(samplePoint, st) - vec3(st * 0.5);
//    samplePoint = vec3((mod(samplePoint, st) - vec3(st * 0.5)).xy, samplePoint.z);
//    vec4 col = texture(iChannel0, samplePoint.xz * 0.01, 0.63);
    vec4 col = getTexColor(samplePoint.xz); //texture(iChannel0, samplePoint.xz * 0.01, -100.0);
//    vec4 col = vec4(random(samplePoint.xz)); //texture(iChannel0, samplePoint.xz * 0.01, -100.0);
    fragmentTextureColor = col;
    return samplePoint.y - dot(col, col) * 3.0;//length(col) * 10.0;// * RMDirection.y;
/*    
    float box = udBox(samplePoint, vec3(1.0, 0.5, 0.125));
    float sphere = sphereSDF(samplePoint + vec3(0.5, 0.0, 0.0), 0.4);
    sphere = min(sphere, sphereSDF(samplePoint + vec3(-0.5, 0.0, 0.0), 0.25));
    return max(box, -sphere);
*/
}

/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end, out float iterations) {
    float depth = start;
    iterations = 0.0;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
        iterations += 1.0;
    }
    return end;
}

mat3 fromEuler(vec3 ang) {
	vec2 a1 = vec2(sin(ang.x),cos(ang.x));
    vec2 a2 = vec2(sin(ang.y),cos(ang.y));
    vec2 a3 = vec2(sin(ang.z),cos(ang.z));
    mat3 m;
    m[0] = vec3(a1.y*a3.y+a1.x*a2.x*a3.x,a1.y*a2.x*a3.x+a3.y*a1.x,-a2.y*a3.x);
	m[1] = vec3(-a2.y*a1.x,a1.y*a2.y,a2.x);
	m[2] = vec3(a3.y*a1.x*a2.x+a1.y*a3.x,a1.x*a3.x-a1.y*a3.y*a2.x,a2.y*a3.y);
	return m;
}

/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN/* + k_s * pow(dotRV, alpha)*/);
}



float shadow(vec3 ro, vec3 rd, vec3 lpos){
    float t = 0.5;
    float d = 0.0;
    float shadow = 1.0;
    for(int iter = 0; iter < 100; iter++){
        d = sceneSDF(ro + rd * t);
        if(d < 0.0001){
            return 0.0;
        }
        if(t > length(ro - lpos) - 0.5){
            break;
        }
        shadow = min(shadow, 128.0 * d / t);
        t += d;
    }
    return shadow;
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0 * sin(iTime),
                          2.0,
                          4.0 * cos(iTime));
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

//    float sh = shadow(p, normalize(p - light1Pos), light1Pos);

//    color -= vec3(0.25 - 0.25 * sh);
//    color *= 1.0 / exp(length(p - eye) * 0.1);
/*    
    vec3 light2Pos = vec3(2.0 * sin(0.37 * iTime),
                          2.0 * cos(0.37 * iTime),
                          2.0);
    vec3 light2Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light2Pos,
                                  light2Intensity);    */
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 dir = rayDirection(80.0, iResolution.xy, mod(fragCoord * SPLIT_VIEWPORT, iResolution.xy));

    vec3 angle = vec3(iTime * 0.1, 0.0, sin(iTime * 0.1) * 1.25);

    mat3 m = fromEuler(angle);
    dir = m * dir;

    vec3 eye = vec3(0.0, 80.0, 0.0) + m * vec3(20.0 * sin(iTime * 0.075), 30.0 * sin(iTime * 0.1), 0.0);
    float iterations;


    RMDirection = dir;
    CameraPosition = eye;

    float dist = shortestDistanceToSurface(eye, dir, MIN_DIST, MAX_DIST, iterations);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        float sky = 1.0 - pow(dir.y, 2.0) * 10.0;
        sky += sin(dir.x * 10.0);
        fragColor = vec4(1.0, sky, 0.0, 0.0) * dir.y;
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * dir;

    vec3 K_a = vec3(0.5, 0.5, 0.5);
    vec3 K_d = vec3(0.9, 0.9, 0.9);
    vec3 K_s = vec3(1.0, 1.0, 1.0);
    float shininess = 10.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye) * fragmentTextureColor.xyz;

    fragColor = vec4(color, 1.0);//+ vec4(0.0125, 0.0, 0.0, 0.0) * iterations;
/*    
    fragColor = (vec4(color, 1.0) +
                 vec4(0.0125, 0.0, 0.0, 0.0) * iterations) *
        		pow((1.0 - (dist / MAX_DIST)), 2.0);
*/

}
