vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 f, in vec2 g )
{
    vec2 uv = 4.*(2. * g - iResolution.xy)/iResolution.y;

    g /= 45.;

    float rep = cos(3.14 *( g.y + g.x* sign(cos(tan( 6e3 * length(floor(g))) )) ));

	float a = atan(uv.x, uv.y) / 3.14159 * 1.5;
	float r = length(uv) - iTime*0.5 ;
	a+=r+rep/2.0;
	uv = abs(fract(vec2(a+r,a-r))-0.5);

	float x = uv.x*1.248;
	float y = uv.y*6.;
	float z = uv.y*1.28;
	float hex = abs(max(x*y,x) - 1.386);
	vec3 col = hex * .3 * vec3(0.15,0.24,0.37)/length(uv);

    vec3 hsv = rgb2hsv(col);
    hsv.x += uv.x;
    hsv.y += uv.y;
    col = hsv2rgb(hsv);

    f.rgb =  col;
    if (abs(rep) > 0.9)
        f.rgb = col+.5-vec3(.34,1,1)/25./rep;
}
