vec2 blend(vec2 c, float s)
{
    c.x *= iResolution.x / iResolution.y;
    vec2 pos = smoothstep(vec2(s), c/4.0, c/5.0*sin(c));
    float i = length(inversesqrt(s));
    vec2 x = pos / i;
    return x * sin(i) * cos(i) * tan(i) + (iTime / 16.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mx = max(iResolution.x, iResolution.y);
	vec2 uv = fragCoord.xy / iResolution.xy - length(mx * fragCoord.xy) / mx / fragCoord.y / fragCoord.x;
    vec2 c = vec2(uv.x + 32.2, uv.y + 32.5);
	fragColor = texture(iChannel0, blend(c, 2.0));
}
