void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 uv = fragCoord.xy / iResolution.xy;

    // how many times to tile in x and y direction
    vec2 n = vec2(6.0, 5.0);
    uv = fract(uv*n);

    // car is in this region
    vec2 uv0 = vec2(0.42, 0.32);
    vec2 uv1 = vec2(0.55, 0.45);

    // transfrom texture coordinates from [0, 1]x[0, 1] range to car region
    uv = uv0 + uv*(uv1 - uv0);

	fragColor = texture(iChannel0, uv);
}
