void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*16.0;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * sin(vec3(iTime, 0.7*iTime, 3.0*iTime)+uv.xyy+vec3(0,2.0,4.0));
    col = col + 0.5 * sin(vec3(2.0* iTime, 0.8*iTime, 0.9*iTime)+uv.xxy+vec3(0,2.0,4.0));

    col = pow(col, vec3(6,3,1));
    // Output to screen
    fragColor = vec4(col,1.0);
}
