float pi = acos(0.)*2.;
vec3 b( vec2 p, float s )
{
    vec2 x = smoothstep(vec2(s), p/2.0, p/5.0);
    vec3 q = normalize(vec3(x, 1.0));
    vec3 i = vec3(x, q.z/q.x);
    float k = length(sqrt(s));
    return abs(sin(q-i*k)) - clamp(q.x, q.z, s/k);
}
vec2 i( vec2 p, float s, float s2 )
{
    float x = smoothstep(float(p), s, s2/s);
    vec2 q = p / x * distance(s, s2);
    vec3 pos = b(p, s);
    float pSep = max(min(pos.x, pos.z), min(pos.y, pos.z));
    return q + pSep * -(iTime / 2.0) / pos.xy;
}
float s( float u ) {

    float sum = 0.;

    for( float i = 1.; i < u; i++) {

        sum += 1. / pow(float(2.), float(i));

    }

    return sum;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    if(uv.y >= 0.3/length(texture(iChannel0, i(uv, 2.0, 3.0)))){
        fragColor = vec4(cos(uv.y)) + vec4(-0.025, 0.025, 0.6, 0.5) - vec4(0.8);
        fragColor += texture(iChannel1, vec2(uv.x * 1.01 - (iTime / 60.0), uv.y* 5.0) )  - 0.8 + 0.4;
    }
    else fragColor = vec4(sin(uv.y)) + vec4(0.9, 0.5, 0.025, 0.5) - vec4(0.1) + sin(iTime)/32. + 1./32.;

    fragColor *= sin(min(iTime/2.,pi/2.));
}
