float plot(float d) {
	return smoothstep(3.0 / iResolution.y, 0.0, abs(d));
}

float line(vec2 p, vec2 a, vec2 b) {
	vec2 d = b - a;
    return abs(dot(normalize(vec2(d.y, -d.x)), p - a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 mo = (iMouse.xy * 2.0 - iResolution.xy) / iResolution.y;
    fragColor = vec4(mix(vec3(1.0), vec3(0.0), plot(line(uv, vec2(0.0), mo))), 1.0);
}
