vec3 grain(vec2 uv, float t, float s) {
    float x = (uv.x + 4.0 ) * (uv.y + 4.0 ) * (t * 10.0);
	vec4 grain = vec4(mod((mod(x, 13.0) + 1.0) * (mod(x, 123.0) + 1.0), 0.01)-0.005) * s;
    return grain.rgb;
}

float curve(vec2 p, float per, float amp, float dx) {
   return p.y-(sin(per*p.x+dx)*amp-2.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ouv = fragCoord.xy / iResolution.xy;
	vec2 uv = ouv * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    uv*=10.;
    float t = iTime;

    //hills
    float hills = curve(uv,.3,2.1,t);
    vec3 hillColor = mix(vec3(0.952, 0.674, 0.415),vec3(0.592, 0.125, 0.211),-ouv.y*hills*0.3);
    hillColor+= (vec3(0.662, 0.564, 0.478)*step(mod(hills,.3),.1))/10.;
    hillColor = mix(hillColor,vec3(0.4),step(0.001,hills));

    //bg hill
    float bghill = curve(uv,.4,2.2,t/2.3);
    vec3 bghillColor = mix(vec3(0.952, 0.674, 0.415),vec3(0.592, 0.125, 0.211),-ouv.y*bghill);
    bghillColor+= (vec3(0.662, 0.564, 0.478)*step(mod(bghill,.3),.1))/10.;
    bghillColor = mix(bghillColor,vec3(0.4),step(0.001,bghill));

    bghillColor/=1.3;

    vec3 c = vec3(0.850, 0.722, 0.517)+grain(ouv,1.0,3.);
    c= mix(c,bghillColor,step(bghill,0.1));
    c= mix(c,hillColor,step(hills,0.1));

    fragColor = vec4(c,1);
}
