// Author: blackpolygon

#define PI 3.14159265359


mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/min(iResolution.x,iResolution.y);
    st.x -= 0.35;

    st -= vec2(0.5);
    st = rotate2d( sin(iTime/(PI*2.))*PI ) * st;    

    float sint = sin(iTime);

    float y = pow(st.y, (0.5 - sint *0.4)*5.);
    vec3 grad = vec3(y);

    vec3 basecolor = vec3(0.080,0.003,0.020);
    vec3 color = grad* vec3(0.149,0.369,0.980);
    vec3 final = basecolor+color;


    float w = 0.5 + sint*0.2    - pow(abs(st.x),0.324);
    vec3 grad2 = vec3(w);

    final = final + grad2;

    float b1a0 = 0.5 + sint*0.5;
    vec2 pos = st;

    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x);
    float f = abs(cos(a*1.))*0.01;

    color = vec3( 1.-smoothstep(f,f+ 0.02+ b1a0*0.10,r) );
	color = color * vec3(0.860,0.759,0.410);
    final = final + color;

     fragColor = vec4(final,1);
}
