void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float dur = 2.;
    float dim = 7.;
    vec2 v = uv;
    v.y=1.-v.y;
    vec2 x = mod(1.-v.xx, 1./dim)+floor(v*dim)/dim;
    float a = .5*(abs(x.x)+abs(x.y));
    float b = fract(iTime/dur);
    a=a>b?0.:1.;
    bool mt = mod(floor(iTime/dur),2.)==0.;
    float cd = a;
    if (mt)
    {
    	cd=1.-cd;    
    }
	fragColor = mix(vec4(a),(mix(texture(iChannel0, uv), texture(iChannel1, uv), cd)), 1.-clamp(iMouse.w,0.,1.));
}
