//calc flow here and add in some trails from previous frames
const vec2 scale = vec2(1.0,1.0);
const float force = 0.6;
const float fade = 0.99;
const float threshold = 0.05;
const float step = 5.0;

float sumRgb(vec3 rgb){
    return (rgb.r + rgb.g + rgb.b)/3.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


    vec2 xOffset = vec2(1.0 / iResolution.x,0.)*step;
	vec2 yOffset = vec2(0.,1.0 / iResolution.y)*step;

	vec4 diff = texture(iChannel0, uv) - texture(iChannel1, uv);

    vec4 xGradient = texture(iChannel0, uv + xOffset) - texture(iChannel0, uv - xOffset);
    vec4 yGradient = texture(iChannel0, uv + yOffset) - texture(iChannel0, uv - yOffset);
	vec4 mag = sqrt((xGradient * xGradient) + (yGradient * yGradient));
    vec4 vx = diff * (xGradient / mag);
    vec4 vy = diff * (yGradient / mag);

    float xAvg = sumRgb(vx.rgb)*force;
    float yAvg = sumRgb(vy.rgb)*force;
    vec2 flow = vec2(xAvg, yAvg);

    //get rid of flow below a certain length
    float strength = length(flow);
    if(strength * threshold > 0.0){
        if(strength < threshold){
        	flow = vec2(0.0);
        } else {
        	strength = (strength - threshold) / (1.0 - threshold);
            flow = normalize(flow) * vec2(strength);
        }
    }

    vec4 last = texture(iChannel2, uv);
    last *= fade;

    fragColor = vec4(flow, 0.0,1.0) + last;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 amt = 1.0 / iResolution.xy;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 flow = texture(iChannel0,uv).rg;
    vec2 offset = vec2(flow.x, flow.y)*amt;
    vec2 coord = offset+uv;

    if(iFrame < 10 || iMouse.z > 0.0 || length(flow.rg)<0.1){
       fragColor = texture(iChannel2, uv);
    } else{
       fragColor = texture(iChannel1, coord  );
    }
}
