float rand(vec2 co){
  return -0.5 + fract(sin(dot(co.xy ,vec2(iTimeDelta + 12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 a = texture(iChannel0, uv).rgb;

    // Grayscale conversion via NTSC conversion weighting
    float gray = 
        clamp(
            dot(a, vec3(0.299, 0.587, 0.114)) + 
            	(iResolution.x - iMouse.x)/iResolution.x * rand(fragCoord.xy),
            0.0, 1.0);

    fragColor = vec4(gray, gray, gray, 1.0);

}
