float kernel[9]=float[9]
(
     0.0, -1.0, 0.0,

    -1.0, 4.0, -1.0,

     0.0, -1.0,  0.0
);
vec2 offset[9]=vec2[9]
(
    vec2(-1.0, -1.0),
    vec2( 0.0, -1.0),
    vec2( 1.0, -1.0),

    vec2( 1.0,  1.0),
    vec2( 1.0,  1.0),
    vec2( 1.0,  -1.0),

    vec2(-1.0,  1.0),
    vec2( 0.0,  1.0),
    vec2( 1.0,  1.0)
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    int i;
    vec3 col = vec3(0.0);
    vec3 result = vec3(0.0);
    for (i = 0; i < 9; i++){
    	col = texture(iChannel0, (fragCoord+offset[i])/iResolution.xy).rgb;
        result += col * kernel[i];
    }

    // Output to screen
    fragColor = vec4(result,1.0);
}
