void mainImage( out vec4 O, vec2 U )
{
    float z = 2.;                                  // zoom factor
    vec2 T = vec2(4.*iTime,0);

    O = vec4( texelFetch(iChannel0, ivec2(U/z+T.yx)%1024,0).r,
              texelFetch(iChannel0, ivec2(U/z+T   )%1024,0).g,
              texelFetch(iChannel0, ivec2(U/z     )%1024,0).b,
              1);

    O = iMouse.z <= 0. ? O * vec4(1,.8,2,0)        // sensitiveness
                       : vec4( (O.x+O.y+O.z)/3. ); // luminance
}
