// Make sure the audio's working! (Chrome is silly now...)

#define N(u) fract(sin(dot(floor(u*128.),vec2(12.13,4.47)))*13.5+iTime)
void mainImage(out vec4 c,vec2 f){
    vec2 u=f/iResolution.y;
    u.y-=mod(u.y,1./128.);
    u*=vec2(exp2(floor(u.y*5.)),u.y);
    float t=texture(iChannel0,vec2(u.y,0)).r-.3,n=N(u)-t;
    u.x/=32.;
    c+=step(0.,n*(t-N(u)));}
