float color = 0.5;
float size = 0.05;

float box(vec2 uv, float col, float x, float y, float w, float h){
    float o = 0.0;
    if( uv.x > x && uv.x < x+w && uv.y > y && uv.y < y + h){
     o = col;   
    }
	return o;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x / iResolution.y;
    vec2 m = iMouse.xy / iResolution.xy;

    // starting positions if mouse not moved
    if(m.x == 0.0){
      m.x = 0.5; 
      m.y = 0.25;
    }

    float b1 = box(uv, 0.5, 0.25-size, 0.625, size, size*aspect);
    float b2 = box(uv, 0.5, 0.75, 0.625, size, size*aspect);
    float b3 = box(uv, 0.5, m.x-size*0.5, m.y-size*0.5, size, size*aspect);

    float all = clamp(b1 + b2 + b3, 0.0,0.5);
    uv.x *=  1.0 - floor(all + 0.5); // remove the black squares from uv
    uv.x += all; // add them back in

    fragColor = vec4(uv.x);
}
