// Vignette properties
const float RADIUS = 0.75;
const float SOFTNESS = 0.45;

// Sepia Color
const vec3 SEPIA = vec3(1.25, 1.0, 0.85); 

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Coordinate
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Texture - the goofy mods are specifically for the nyan cat provided
    vec4 texColor = texture(iChannel0, vec2(uv.s * 0.15, 1.0 - uv.t / 1.0));

    //		1. VIGNETTE

    // Center
    vec2 position = uv - vec2(0.5);

    // Center position length
    float len = length(position);

    // Smoothing the gradient
    float vignette = smoothstep(RADIUS, RADIUS-SOFTNESS, len);

    // 50% vignette opacity
    texColor.rgb = mix(texColor.rgb, texColor.rgb * vignette, 0.5);

    //		2. GRAYSCALE

    // Grayscale conversion using NTSC conversion weights
    float gray = dot(texColor.rgb, vec3(0.299, 0.587, 0.114));

    //		3. SEPIA

    // Create sepia
    vec3 sepiaColor = vec3(gray) * SEPIA;

    // Mix the opcatiy to 75%
    texColor.rgb = mix(texColor.rgb, sepiaColor, 0.75);

    // Final color
    fragColor = texColor;
}
