// Minified
void mainImage( out vec4 f, in vec2 w )
{
    vec2  p = 2.*w/iResolution.xy - 1.;
    f = vec4( fract( atan(p.y,p.x) + 0.5*iTime/12. - 120.0*log(length(p))) > 0.5 );
}

