{
	#define lineThickness 3.0

    vec2 uv = fragCoord.xy / iResolution.xy;

    float mult;

    if ( fragCoord.x < fragCoord.y + lineThickness && fragCoord.x > fragCoord.y - lineThickness) {
        mult = 1.;
    } else {
        mult = 0.;
