void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x += 0.05*sin(iTime*1.0)*(1.0-uv.y);

    float vs = texture( iChannel0, vec2( uv.x*0.01, uv.y*2.0 )+iTime*0.01 ).r;
    float ns = texture( iChannel0, vec2( uv.x*0.01, uv.y*0.6 )+iTime*0.01 ).r;
    float ps = texture( iChannel0, uv*0.1+iTime*0.3 ).r;
    float ls = smoothstep( 0.7, 1.0, texture( iChannel0, vec2(iTime)*0.1 ).r  );

    float line = smoothstep( 0.998, 1.0, 1.0-abs(uv.x-0.5+(vs*0.2+ns)*0.02-0.01) ) *ps;
    float glow = smoothstep( 0.90, 1.0, 1.0-abs(uv.x-0.5+ns*0.02-0.01) ) *ps;





	fragColor = vec4( vec3(line*0.9+glow*0.1)*ls*3.0, 1.0);
}
