#define PI 3.14159265359
#define PI2 2.*PI
#define HPI PI/2.

//moving
#define BIAS sin(iTime)*.5+.5
//#define BIAS .6

#define GAIN sin(iTime)*.5+.5
//#define GAIN .6

float GetBias(float x,float bias)
{
  return (x / ((((1.0/bias) - 2.0)*(1.0 - x))+1.0));
}

float GetGain(float x,float gain)
{
  if(x < 0.5)
    return GetBias(x * 2.0,gain)/2.0;
  else
    return GetBias(x * 2.0 - 1.0,1.0 - gain)/2.0 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float strock =.01;

    float x = uv.x; // [-1.0,1.0]

    float b = GetBias(x,BIAS);

    float g = GetGain(x,GAIN);

    //x = b;
    x = g;



    float f = sin((x-.5) * PI);


    f = f*.5 + .5;
	float st = step(f -.005,uv.y) * step(uv.y, f + 0.005);
	fragColor = vec4(vec3(1.),1.) * st;
}
