void mainImage( out vec4 fragColor, in vec2 fragCoord ) {


     float crayStationsCoef = .01;     
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    p.x +=  sin(pow(iTime,.5))*crayStationsCoef*10.0;    
    //p.y +=  cos(iTime)*crayStationsCoef*10.0;
    // angle of each pixel to the center of the screen
    float a = atan(p.y,p.x) + iTime * sin(iTime)*.002 + iMouse.x * .0001;


    float exp = 1.; // makes this a box if you see it fit
    //Mink.. Calculation 
    float r = pow( pow(p.x*p.x,exp) + pow(p.y*p.y,exp), 1.0/(exp * 2.0) ) + sin(iTime)*crayStationsCoef;
    a += pow(r,.5); 

    // index texture by angle and radious, and animate along radius    
    vec2 uv = vec2(0.5/r + 0.5*iTime + iMouse.y,                    a/3.1416 );

    // Lighting
    r -= sin(iTime)*1.05;

    // fetch color and darken in the center
    //vec3 col =  texture( iChannel0, uv ).xyz * r;

    //Lerp in Cray Mode
    //col = (crayStationsCoef) * HSVtoRGB(vec3(col.r, 1.0, 1.0)) + col *(1.-crayStationsCoef); 	//    col *= (sin(uv.x*5.0)*1.0 + 1.0) * .7;                  

    vec4 pe = vec4(fragCoord,0.,1.)/iResolution.xyxy-.3, d=pe, t, c;
    pe.x = sin(iTime) * pe.x * 10. + 29. ;
    pe.y = sin(iTime) * pe.y * 100.;
    pe.xy = pe.xy;
    pe.yz -= iTime*10.;
    p.x += iTime * 100.;
    for(float i = 3.; i >0.; i-=.01)
    {
        t = mod(pe, 32.)-4.;
        c = texture(iChannel0, t.xyz*sign(t.xyz));
        t = min(length(t.xy), length(t.xz))-4.5+c;
        c = c*i+i*.1*i;
        fragColor = vec4(1.6 - c.x, .3, sin(iTime)*.5+c.z*3.,1.);
        if(t.x<.01) break;
        pe -= d*t.x*.2;
     }
}
