void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
 vec2 position = (fragCoord.xy/iResolution.xy) - 0.5;
    float time = iTime;

    //nodrosina beatu
    float beat = texture( iChannel1, vec2(0.0, 0.01 ) ).x;

    //cilpa pa y asi
    float y = 2. * position.y * sin(400.0 * position.y - 20.0 * time *0.01);
 y = 1. / (1000. * abs(position.x - y));

    //cilpa pa x asi
 float x = 2. * position.x * sin(500.0 * position.x - 20.0 * time *0.01);
    x = 1. / (1000. * abs(position.y - x));

    //spiideklis viduu
    float saule = 5./length(50. / beat*length(position - vec2(0, 0)));

 	//saules atrashanaas vieta.
    vec4 vsaule = vec4(saule, saule*2., saule, 1.0);
 vec4 vstari = vec4((position.y+position.x)*0.5 - y, (y+x)*7., y+x, 1.0);

    //realizaacija
    fragColor = mix(vsaule, vstari, 0.5)-0.3; //-0.3 ir spiidekla spozums jeyyb mirdzums
}
