void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 textureColor = texture(iChannel0, vec2(uv.x, uv.y * -1.0));

    float lightness = (textureColor.x + textureColor.y + textureColor.z) / 3.0;

//	fragColor = vec4(0.444013 * lightness, 0.51036 * lightness, 0.229662 * lightness, 1.0);
	fragColor = vec4(0.87 * lightness, lightness, 0.45 * lightness, 1.0);
}
