/*  s - size of circle
	a - angle
	c - center of circle
	d - distance from current pixel to center
    l - biggest circle brightness */

void mainImage(out vec4 b, in vec2 f) {

    float 
        t = iTime,
        l = 0., s, a;

    for (float k = 0.; k < 76.; k++) {

        s = pow(fract(k / 36. + t / 4.), 5.),
		a = (k / 1.2 + t);

        if (length(vec2(sin(a), cos(a)) * (s * s + s) + .5 - f.xy / iResolution.xy) < s) l = max(s, l);

    }

    b = l * vec4(.63,.45,.45, .5);    
}
