void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 tex0 = texture(iChannel0,uv);

    float sY = sin(fragCoord.y + t * 2.0);
    float sX = sin(fragCoord.x + t * 60.0);

    float c = uv.y;
  	c += sY - 0.4;

    float a = sX;

    c -= 4. * sin(t * 4.0 + uv.y * 20.0);
   	c += 1. * sin(t * 1.0 + uv.y * 40.0);
    c += 1. * sin(t * 4.0 + uv.x * 4.0);

    c += fract(sin(dot(uv.xy ,vec2(13.18,78.233))) * 338.5453);

    float r = c - 0.13;
    float g = c - 0.1;
    float b = c ;

    vec4 final = vec4(r,g,b,1.0);
    final.rgb += vec3(0.4,0.4,0.4);

    fragColor = (tex0*5.0) + final;
}
