void mainImage( out vec4 fragColor, in vec2 i )			{
	vec2 uv = i.xy / iResolution.xy;
	vec2 center = vec2(.5, .5);
	float d = distance(uv, center);
	float t = tan(iTime) * .75;

	float diff = d - t;
	diff *= .75;
	float m = max(1. - abs(diff * 1. / (.15 * clamp(iTime / .2, 0., 1.))), 0.);
	m *= diff;

	vec2 diffUV = normalize(uv - center);

	fragColor = texture(iChannel0, uv + diffUV * m);
}
