const float amt = 30.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float bars = iResolution.x / amt;

    //x bars
    float s = step(0.0, sin(uv.y *bars));
    float nuv = uv.x;
    nuv *= s;
	float uuv = 1.0 -uv.x;
    uuv *= (1.0 - s);
    uv.x = mix(uuv, nuv, s);


    //y bars
    float s2 = step(0.0, sin(uv.x *(bars * (iResolution.x / iResolution.y))));
    float nuv2 = uv.y;
    nuv2 *= s2;
	float uuv2 = 1.0 -uv.y;
    uuv2 *= (1.0 - s2);

    uv.y = mix(uuv2, nuv2, s2);


	fragColor = texture(iChannel0, uv);
    fragColor.a = 1.0;
}
