// Houses info
vec2 HousePlacement = vec2(0.5, 0.15);
float houseLength = 0.3;
float houseWidth = 0.25;

//Chimney Info
vec2 ChimneyPlacement = vec2(0.23, 0.45);
float chimneyLength = 0.03;
float chimneyWidth = 0.05;

//Window info
vec2 Window1 = vec2(0.4, 0.3);
vec2 Window2 = vec2(0.6, 0.3);
vec2 Window3 = vec2(0.4, 0.1);
vec2 Window4 = vec2(0.6, 0.1);
float windowLength = 0.05;
float windowWidth = 0.05;

//Color info
vec4 yellow = vec4(1.0, 1.0, 0.0, 1.0);
vec4 brown = vec4(0.4, 0.24, 0.24, 1.0);
vec4 gray = vec4(0.6, 0.56, 0.56, 1.0);
vec4 darkBlue = vec4(0.0, 0.0, 0.4, 1.0);

//Switchs the layers
float HouseOn = 1.0;
float ChimneyOn = 1.0;
float WindowOn = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.y *= iResolution.y / iResolution.x;

    vec4 BackGround = darkBlue;
	vec4 House = brown;
    vec4 Chimney = gray;
    vec4 Windows = yellow;

    House *= step(length(uv.x - HousePlacement.x), houseLength) * 
        	 step(length(uv.y - HousePlacement.y), houseWidth);

    House *= HouseOn;

    Chimney *= step(length(uv.x - ChimneyPlacement.x), chimneyLength) * 
        	   step(length(uv.y - ChimneyPlacement.y), chimneyWidth) * ChimneyOn;

    Windows  *=  (step(length(uv.x - Window1.x), windowLength) * 
        		 step(length(uv.y - Window1.y), windowWidth) +
        		 step(length(uv.x - Window2.x), windowLength) * 
        		 step(length(uv.y - Window2.y), windowWidth) +
                 step(length(uv.x - Window3.x), windowLength) *
                 step(length(uv.y - Window3.y), windowWidth) +
                 step(length(uv.x - Window4.x), windowLength) *
                 step(length(uv.y - Window4.y), windowWidth)) * WindowOn;

    vec4 Composite = mix(BackGround, House, House.a);
    Composite = mix(Composite, Chimney, Chimney.a);
    Composite = mix(Composite, Windows, Windows.a);

    fragColor = Composite;
}
