float rectangle(vec2 uv, vec2 pos, vec2 size) {
    return 1.-clamp(length(max(abs(uv-pos)-size,0.))*400.,0.,1.);
}

float rand(float p0,float p1){
    vec2 co = vec2(p0,p1);
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec3 draw(vec2 uv, vec2 suv, float time) {
    //Red part
    float a = rectangle(uv,vec2(0.55,0.5),vec2(0.2,0.15));
    a += clamp(1.-(length(suv-vec2(0.22,((0.231)*uv.y)+0.3))-0.2)*400.,0.,1.);
    a = clamp(a,0.,1.);
    a -= rectangle(uv,vec2(0.55,0.2),vec2(0.8,0.18));
    a = clamp(a,0.,1.);
    //Wheels
    float b = clamp(1.-(length(suv-vec2(0.2,0.35))-0.05)*400.,0.,1.);
    b += clamp(1.-(length(suv-vec2(0.65,0.35))-0.05)*400.,0.,1.);
    b = clamp(b,0.,1.);
    vec3 colors = vec3(a,0.,0.);
    colors -= vec3(b,0.,0.);
    colors = clamp(colors,0.,1.);
    colors += vec3(b*0.15,b*0.1,b*0.1);
    float mask = clamp(a+b,0.,1.);
    vec3 bg = vec3(rand(floor(time*5.),0.1),rand(floor(time*5.),0.2),rand(floor(time*5.),0.3));
    vec3 almostfinished = clamp((bg-mask),0.,1.)+colors;
    float c = step(fract((uv.x*6.)+time),0.5);
    c -= 1.-step(uv.y,0.3);
    c -= step(uv.y,0.27);
    c = clamp(c,0.,1.0);
    float c2 = clamp(step(fract((suv.x*80.)+time),0.5)*step(fract((suv.y*80.)+time),0.5),0.,1.);
    c2 += clamp(step(fract((suv.x*80.)+time+0.5),0.5)*step(fract((suv.y*80.)+time+0.5),0.5),0.,1.);
    c2 = clamp(c2,0.,1.);
    float c3 = step(uv.y,0.3);
    float c4 = step(uv.y,0.25);
    vec3 finished = clamp(almostfinished-c3+c4-(c2*c4),0.,1.);
    finished += vec3(c2*c4,0.,0.);
    finished += (c3-c4)*0.5;
    finished += (c);
    finished = clamp(finished,0.,1.);
    return vec3(finished);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float time = iTime;
    vec2 suv = vec2(((uv.x-0.5)*(iResolution.x / iResolution.y))+0.5,uv.y);
	vec3 finish = draw(uv,suv,time);

	fragColor = vec4(finish,1.0);
}

float sin1(float val) {
    return fract(val/3.1415926535);
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float sin2(float val) {
    return clamp(sin(val*4.)*20.,-1.,1.)+fract(val/3.1415926535);
}

float music(float time) {
    float noise = fract(sin((floor(time*5.)/5.) *(12.9898+78.233)) * 43758.5453);
    float a = sin2(time*(300.+(noise*1100.)));
    noise = fract(sin((floor((time+132.)*5.)/5.) *(12.9898+78.233)) * 43758.5453);
    a += sin2(time*(100.+(noise*1200.)));
    a /= 4.;
    return ( a );
}

vec2 mainSound( in int samp,float ti1)
{
    float time = ti1*1.2;
    float a = music(time);
    return vec2( a );
}
