void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	float n = iTime/4.;
	float x = uv.x*0.25;
	float y = uv.y*0.25;

	float xcir = x-0.125;
	float ycir = y+(cos(n)/4.)-0.125-(sin((x*8.+n))/5.);

	float circle = ((xcir*xcir)+(ycir*ycir));
	float tunnel = sqrt(circle*0.125)/circle*0.3+n*0.4;
	float wally = fract(atan(xcir/ycir*1.5)*1.5);

	float shade=clamp(atan(circle*8.0),0.0,1.5);
	float full=(fract(tunnel)*wally);
    vec3 f = texture(iChannel0,vec2(
        tunnel
        ,
        wally
    )).rgb;
	fragColor = vec4(pow(f.r*(shade),0.7),pow(f.g*(shade),0.6),pow(f.b*(shade),0.5),1.0);
}
