void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float rad = 100.0 + iTime;
    float thick = 0.8 + rad;
    float thick2 = 40.0 + thick;
    float disp = 35.0;
    float disp2 = 30.0;

	vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    float xDist = abs(fragCoord.x - (iResolution.x/2.0));
    float yDist = abs(fragCoord.y - (iResolution.y/2.0));

    float dist = sqrt((xDist * xDist) + (yDist * yDist));

    if(dist > rad && dist < thick){
        fragColor = vec4(1.0,1.0,1.0,1.0);
    }
    else if(dist > thick && dist < thick2){
        float frac = (dist - thick);
        //frac = 2.0 - frac;
        frac = (disp - frac) / disp2;
		fragColor = vec4(frac,frac,frac,1.0);

    }
    /*
    else if(dist < rad){
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }*/
    else{
    	//fragColor = vec4(0.1,0.1,0.1,1.0);
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }

    //TODO: add chromatic abberation on edge?

}
