void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = I/iResolution.xy;

    float v = texture(iChannel0, uv).r;

    vec3 c;
    if(uv.y>.5)
    	c = 2.*vec3(max(v-0.5,0.), 0., max(0.5-v,0.));
    else
    	c = vec3(v);

    O = vec4(c, 1.);
}

void mainImage( out vec4 fragColor, in vec2 I )
{
    vec2 uv = I/iResolution.xy;

    ivec2 ifragCoord = ivec2(I);

    float v = texelFetch(iChannel1, ifragCoord+ivec2(1,0), 0).r;

    if(abs(I.x-iResolution.x) < 1.)
    	v = texture(iChannel0, vec2(fract(uv.y*2.), uv.y>0.5?1.:0.)).r;


    fragColor = vec4(v);
}
