void mainImage( out vec4 color, in vec2 pixCoords )
{
	vec2 uv = pixCoords.xy / iResolution.xy;
	float zoom = (iResolution.x / 20.0);

    float cameraX = 7.0* cos(iTime)*0.9; 
    float cameraY = 4.0* cos(iTime*0.9); 
    float x = (pixCoords.x / zoom) - cameraX;
    float y = (pixCoords.y / zoom) - cameraY;
 	float fx=cos(x);
        if (y<fx){
            color=vec4(0.0,0.0,1.0,1.0);
        }
    else color=vec4(0.5,0.6,0.2,1.0);



}
