void mainImage( out vec4 pixelColor, in vec2 pixelCoord )
{
	float zoom = (iResolution.x / 20.0);
    float cameraX = iTime * 10.0;
    float cameraY = iTime * 2.0;

    float x = (pixelCoord.x / zoom) - cameraX;
    float y = (pixelCoord.y / zoom) - cameraY;

    float theta = iTime;

    x = x*cos(theta) + y*sin(theta);
    y = -x*sin(theta) - y*cos(theta);

    //White background
    pixelColor = vec4(1.0,1.0,1.0,1.0);

    //Axis
    {
    float width = 0.02;
    if ((x < width && x > -width) || (y < width && y > -width)) 
    {    
        pixelColor = vec4(1.0,0.0,0.0,1.0);
        return;
    }
    if ((mod(x,1.0) < width && mod(x,1.0) > -width) || (mod(y,1.0) < width && mod(y,1.0)> -width)) pixelColor = vec4(0.5,0.5,0.5,1.0);
    }

    //Circles
    {
    float centerX = 0.5;
    float centerY = 0.5;
    float dX = mod(x,1.0) - centerX;
    float dY = mod(y,1.0) - centerY;
    float dist = sqrt(dX * dX + dY * dY);
    float radius = 0.3 + 0.2 * cos(x);
    float color1 = 0.5 + cos(iTime) * 0.5;
    float color2 = 0.5 + cos(iTime + 1.0) * 0.5;
    float color3 = 0.5 + cos(iTime + 2.0) * 0.5;
    if (dist < radius) pixelColor = vec4(color1,color2,color3,1.0);
    }

}
